
public class MethodDemo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MethodDemo4 m1= new MethodDemo4();
		m1.addition();
		m1.subtraction();
		m1.multiplication();
	}
	void addition()
	{
		int a=10;
		int b=20;
		int c=a+b;
		System.out.println(c);
	}
	
	void subtraction()
	{
		int a=10;
		int b=20;
		int c=b-a;
		System.out.println(c);
	}
	
	void multiplication()
	{
		int a=10;
		int b=20;
		int c=a*b;
		System.out.println(c);
	}
	

}
